import { BCard, BCardTitle, BCardFooter, BButton, BProgressBar, BProgress, BBadge, BTooltip, BRow, BCol, VBModal } from 'bootstrap-vue';
import Ripple from 'vue-ripple-directive';
import { percent, tokenFormatter } from '@/libs/utils';
import dayjs from 'dayjs';
import OperationModal from '@/views/components/OperationModal/index.vue';
export default {
  components: {
    BCard,
    BButton,
    BCardFooter,
    BProgressBar,
    BProgress,
    BBadge,
    BCardTitle,
    BTooltip,
    BRow,
    BCol,
    OperationModal
  },
  directives: {
    'b-modal': VBModal,
    Ripple
  },
  props: {
    p: {
      type: Object,
      default: () => ({})
    }
  },
  methods: {
    selectProposal(modal, pid, title) {
      this.$parent.operationModalType = modal;
      this.$parent.selectedProposalId = Number(pid);
      this.$parent.selectedTitle = title;
    },

    formatType(v) {
      const txt = String(v).replace('Proposal', '');
      const index = txt.lastIndexOf('.');
      return index > 0 ? txt.substring(index + 1) : txt;
    },

    percent: v => percent(v),
    formatDate: v => dayjs(v).format('YYYY-MM-DD'),
    formatToken: v => tokenFormatter(v, {})
  }
};
